DROP TABLE IF EXISTS user_picks;
DROP TABLE IF EXISTS profiles;
DROP TABLE IF EXISTS users CASCADE;
DROP TABLE IF EXISTS bowl_games;
DROP TABLE IF EXISTS bowls;
DROP TABLE IF EXISTS teams;
DROP TABLE IF EXISTS conferences;

CREATE TABLE users (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    email VARCHAR NOT NULL UNIQUE,
    password_hash VARCHAR NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE profiles (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    user_id BIGINT NOT NULL,
    first_name TEXT,
    last_name TEXT,
    avatar VARCHAR,
    user_name TEXT,
    primary_color TEXT,
    secondary_color TEXT,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users (id)
);

CREATE TABLE bowls (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    name TEXT NOT NULL,
    default_points INTEGER,
    stadium TEXT,
    location TEXT,
    payout TEXT,
    current_sponsor TEXT,
    first_year DATE,
    last_year DATE,
    alternate_names TEXT,
    image VARCHAR
);

CREATE TABLE conferences (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    name TEXT NOT NULL,
    short_name TEXT NOT NULL,
    image VARCHAR,
    primary_color TEXT,
    secondary_color TEXT
);

CREATE TABLE teams (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    name TEXT NOT NULL,
    abbreviation TEXT,
    conference_id BIGINT,
    mascot TEXT,
    primary_color TEXT,
    secondary_color TEXT,
    tertiary_color TEXT,
    helmet_image VARCHAR,
    image VARCHAR,
    current BOOLEAN NOT NULL DEFAULT FALSE,
    year_from INTEGER,
    year_to INTEGER,
    bowl_games INTEGER,
    bowl_wins INTEGER,
    bowl_losses INTEGER,
    bowl_ties INTEGER,
    FOREIGN KEY (conference_id) REFERENCES conferences (id)
);

CREATE TABLE bowl_games (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    status_id INTEGER NOT NULL,
    bowl_id BIGINT NOT NULL,
    year INTEGER NOT NULL,
    winner_id BIGINT,
    point_total INTEGER,
    home_team_id BIGINT,
    away_team_id BIGINT,
    home_team_score INTEGER,
    away_team_score INTEGER,
    attendance INTEGER,
    mvp TEXT,
    sponsor TEXT,
    game_date DATE,
    day_of_week TEXT,
    FOREIGN KEY (bowl_id) REFERENCES bowls (id),
    FOREIGN KEY (winner_id) REFERENCES teams (id),
    FOREIGN KEY (home_team_id) REFERENCES teams (id),
    FOREIGN KEY (away_team_id) REFERENCES teams (id)
);

CREATE TABLE user_picks (
    id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    status_id BIGINT NOT NULL,
    user_id BIGINT NOT NULL,
    bowl_game_id BIGINT NOT NULL,
    pick_id BIGINT,
    FOREIGN KEY (user_id) REFERENCES users (id),
    FOREIGN KEY (bowl_game_id) REFERENCES bowl_games (id)
);







